mapper.insert:

	SELECT NVL(MAX(crtroom_no), 0) + 1
	FROM courtroom		   

INSERT INTO crtroom
	(
		crtroom_no
		, host_no
		, crtroom_name
		, crtroom_exp
		, crtroom_region
		, crtroom_location
		, crtroom_operate_time
		, crtroom_caution
		, crtroom_use_guide
		, crtroom_indoor_outdoor
		, crtroom_regi_date
	)
	VALUES
	(
		#{crtroomNo}
		, #{hostNo}
		, #{crtroomName}
		, #{crtroomExp}
		, #{crtroomRegion}
		, #{crtroomLocation}
		, #{crtroomOperateTime}
		, #{crtroomCaution}
		, #{crtroomUseGuide}
		, #{crtroomIndoorOutdoor}
		, SYSDATE
	)
