mapper.insert:

 		SELECT NVL(MAX(recruit_no),0)+1
 		FROM club_match_recruit_mem

 	INSERT INTO club_match_recruit_mem
 			(
 				mem_no
 				, club_match_recruit_no
 				, recruit_no
			)
 		   VALUES
 		   (
 		   	#{memNo}
 		   	, #{clubMatchRecruitNo}
 		   	, #{recruitNo}
 		   )

 	INSERT INTO club_match_rst
 			(
 				club_match_no
 				, club_match_rst
			)
 		   VALUES
 		   (
 		   	#{clubMatchNo}
 		   	, #{clubMatchRst}
 		   )
