mapper.insert:

  			SELECT NVL(MAX(club_post_no),0)+1
  			FROM club_post

  		INSERT INTO club_post
  	 			(
  	 				club_post_no
	 			   , club_post_title
	 			   , club_post_ctt
	 			   , club_post_write_date
	 			   , mem_id
	 			   , club_no
  	 			)
  	 			VALUES
  	 			(
  	 				#{clubPostNo}
  	 				,#{clubPostTitle}
  	 				,#{clubPostCtt}
  	 				,SYSDATE
  	 				,#{memId}
  	 				,#{clubNo}
  	 			)

mapper.update:
UPDATE club_post
	SET
		club_post_title = #{clubPostTitle}
		, club_post_ctt = #{clubPostCtt}
		, club_post_edit_date = SYSDATE
WHERE club_post_no = #{clubPostNo}
AND mem_id = #{memId}

UPDATE 	club_post
SET		club_post_hit = club_post_hit + 1
WHERE	club_post_no = #{clubPostNo}
