mapper.insert:

  			SELECT NVL(MAX(club_post_no),0)+1
  			FROM club_post

  		INSERT INTO club_post
  	 			(
  	 				club_post_no
	 			   , club_post_title
	 			   , club_post_ctt
	 			   , club_post_write_date
	 			   , mem_id
	 			   , club_no
  	 			)
  	 			VALUES
  	 			(
  	 				#{clubPostNo}
  	 				,#{clubPostTitle}
  	 				,#{clubPostCtt}
  	 				,SYSDATE
  	 				,#{memId}
  	 				,#{clubNo}
  	 			)
