mapper.insert:

	SELECT NVL(MAX(club_rep_no), 0) + 1 
	FROM club_rep		   

INSERT INTO club_rep
	  (
		club_rep_no
 	    , club_rep_ctt
 	    , club_rep_write_date
 	    , club_post_no
 	    , mem_id
	  )
	   VALUES
	  (
		#{clubRepNo}
 	    , #{clubRepCtt}
 	    , SYSDATE
 	    , #{clubPostNo}
 	    , #{memId}
	  )

mapper.delete:
DELETE FROM club_rep
WHERE club_rep_no = #{clubRepNo} 
AND mem_id = #{memId}
