mapper.select:
select * from ( select A.* , rownum rn from (
SELECT c.crtroom_no , c.host_id , c.crtroom_name , c.crtroom_exp , c.crtroom_region , c.crtroom_location , c.crtroom_operate_start_time , c.crtroom_operate_end_time , c.crtroom_caution , c.crtroom_use_guide , c.crtroom_regi_date , FIND_CATEGORY_NAME(c.crtroom_region) crtroom_region_name , MIN(d.crt_use_price) min_price , a.attach_path , TRUNC(SUM(r.review_star)/COUNT(r.review_no)) avg_star , COUNT(r.review_no) count_review FROM crtroom c JOIN crt_detail_inf d ON c.crtroom_no = d.crtroom_no JOIN attach a ON c.crtroom_no = a.attach_table_pk LEFT OUTER JOIN review r ON c.crtroom_no = r.crtroom_no WHERE a.attach_table_div = 'cd' AND a.attach_turn = 1 AND c.crtroom_state = 'm2' AND c.crtroom_region = #{crtroomRegion} GROUP BY c.crtroom_no , c.host_id , c.crtroom_name , c.crtroom_exp , c.crtroom_region , c.crtroom_location , c.crtroom_operate_start_time , c.crtroom_operate_end_time , c.crtroom_caution , c.crtroom_use_guide , c.crtroom_regi_date , a.attach_path ORDER BY ${sort}
) A) b where rn between (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

mapper.insert:

	SELECT NVL(MAX(crtroom_no), 0) + 1
	FROM crtroom		   

INSERT INTO crtroom
	(
		crtroom_no
		, host_id
		, crtroom_name
		, crtroom_exp
		, crtroom_region
		, crtroom_location
		, crtroom_operate_start_time
		, crtroom_operate_end_time
		, crtroom_caution
		, crtroom_use_guide
		, crtroom_regi_date
		, crtroom_state
	)
	VALUES
	(
		#{crtroomNo}
		, #{hostId}
		, #{crtroomName}
		, #{crtroomExp}
		, #{crtroomRegion}
		, #{crtroomLocation}
		, #{crtroomOperateStartTime}
		, #{crtroomOperateEndTime}
		, #{crtroomCaution}
		, #{crtroomUseGuide}
		, SYSDATE
		, 'm3'
	)

mapper.update:
UPDATE crtroom
	SET
		crtroom_name = #{crtroomName}
		, crtroom_exp = #{crtroomExp}
		, crtroom_region = #{crtroomRegion}
		, crtroom_location = #{crtroomLocation}
		, crtroom_operate_start_time = #{crtroomOperateStartTime}
		, crtroom_operate_end_time = #{crtroomOperateEndTime}
		, crtroom_caution = #{crtroomCaution}
		, crtroom_use_guide = #{crtroomUseGuide}
	WHERE crtroom_no = #{crtroomNo}

mapper.delete:
DELETE FROM attach
WHERE attach_table_div = 'co'
AND attach_table_pk = #{crtroomNo}

mapper.select:
		SELECT TRUNC(SUM(review_star)/COUNT(review_no)) avg_star, COUNT(review_no) count_review
        FROM review
        WHERE crtroom_no = #{crtroomNO}

mapper.insert:

  			SELECT NVL(MAX(review_no), 0) + 1
  			FROM review		   

  		INSERT INTO review
  			(
