mapper.delete:
DELETE FROM attach
WHERE attach_table_div = 'cr'
AND attach_table_pk = #{reviewNo}

mapper.update:
UPDATE crtroom
	SET crtroom_state = 'm2'
WHERE crtroom_no = #{crtroomNo}

UPDATE crtroom
	SET crtroom_state = 'm1'
WHERE crtroom_no = #{crtroomNo}
