mapper.insert:

	SELECT NVL(MAX(crt_detail_no),0) + 1
	FROM crt_detail_inf		   

INSERT INTO crt_detail_inf
	(
		crt_detail_no
		, crt_use_price
		, use_unit_time
		, crt_indoor_outdoor
		, crtroom_no
	)
VALUES
	(
		#{crtDetailNo}
		, #{crtUsePrice}
		, #{useUnitTime}
		, #{crtIndoorOutdoor}
		, #{crtroomNo}
	)

mapper.update:
UPDATE crt_detail_inf
	SET
			crt_use_price = #{crtUsePrice}
			, use_unit_time = #{useUnitTime}
			, crt_indoor_outdoor = #{crtIndoorOutdoor}
	WHERE crt_detail_no = #{crtDetailNo}
