mapper.insert:

	SELECT  (SELECT NVL(MAX(reserve_no),0)+1
			  FROM reserve) reserve_no,
			(SELECT NVL(MAX(reserve_detail_no),0)+1
			   FROM reserve_detail_inf) reserve_detail_no
	FROM dual

 INSERT ALL INTO reserve
(
	RESERVE_NO
	, RESERVE_PRICE
	, CRTROOM_NO
	, CRT_DETAIL_NO
	, MEM_ID
	, RESERVE_PAY_NO
	, RESERVE_UID
)
VALUES
(
	#{reserveNo}
	, #{reservePrice}
	, #{crtroomNo}
	, #{crtDetailNo}
	, #{memId}
	, #{reservePayNo}
	, #{reserveUid}
)
INTO reserve_detail_inf
(
	RESERVE_DETAIL_NO
	, RESERVE_DATE
	, RESERVE_TIME
	, RESERVE_NO
	, T_RESERVE_TIME
)
VALUES
(
	#{reserveDetailNo}
	, #{reserveDate}
	, #{reserveTime}
	, #{reserveNo}
	, #{tReserveTime}
)
SELECT * FROM dual
