mapper.select:
select * from ( select A.* , rownum rn from (
SELECT m.match_no, m.mem_id, m.match_date, m.time, m.game_way, FIND_CATEGORY_NAME (m.game_way) game_way_name, m.prog_state, FIND_CATEGORY_NAME (m.prog_state) prog_state_name, m.ntrp, m.lv, m.gen, FIND_CATEGORY_NAME (m.gen) gen_name, m.crt_use_amt, m.recruit_psn_num, m.detail_ctt, m.age, m.in_date, m.update_date, c.crtroom_no, c.crtroom_name, c.crtroom_location, FIND_CATEGORY_NAME (c.crtroom_location) crtroom_location_name FROM match m, crtroom c WHERE m.crtroom_no = c.crtroom_no AND TRUNC (match_date) = TRUNC(#{matchDate}) and game_way = #{gameWay} and gen = #{gen} and crtroom_location = #{crtroomLocation} and progState = #{progState} ORDER BY time
) A) b where rn between (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

select count(*)
FROM match m, crtroom c
WHERE m.crtroom_no = c.crtroom_no
AND TRUNC (match_date) = TRUNC(#{matchDate})
and game_way = #{gameWay}
and gen = #{gen}
and crtroom_location = #{crtroomLocation}
and progState = #{progState}

select * from ( select A.* , rownum rn from (
SELECT m.match_no , m.club_name , m.match_date , m.time , m.game_way , FIND_CATEGORY_NAME (m.game_way) game_way_name , m.prog_state , FIND_CATEGORY_NAME (m.prog_state) prog_state_name , m.recruit_psn_num , m.club_no , m.ntrp , FIND_CATEGORY_NAME (m.ntrp) ntrp_name , m.lv , FIND_CATEGORY_NAME (m.lv) lv_name , m.crt_use_amt , m.detail_ctt , m.crtroom_no , m.age , c.crtroom_name , c.crtroom_location , FIND_CATEGORY_NAME (c.crtroom_location) crtroom_location_name FROM club_match m, crtroom c WHERE m.crtroom_no = c.crtroom_no AND TRUNC (match_date) = TRUNC(#{matchDate}) and game_way = #{gameWay} and ntrp = #{ntrp} and lv = #{lv} and crtroom_location = #{crtroomLocation} and progState = #{progState} ORDER BY time
) A) b where rn between (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

select count(*)
FROM club_match m, crtroom c
WHERE m.crtroom_no = c.crtroom_no
AND TRUNC (match_date) = TRUNC(#{matchDate})
and game_way = #{gameWay}
and ntrp = #{ntrp}
and lv = #{lv}
and crtroom_location = #{crtroomLocation}
and progState = #{progState}

select * from ( select A.* , rownum rn from (
SELECT m.match_no , m.cont_name , m.start_date , m.end_date , m.time , m.prog_state , FIND_CATEGORY_NAME (m.prog_state) prog_state_name , m.recruit_psn_num , m.ntrp , FIND_CATEGORY_NAME (m.ntrp) ntrp_name , m.lv , m.cont_attend_cost , m.detail_ctt , m.crtroom_no , m.age , c.crtroom_name , c.crtroom_location , FIND_CATEGORY_NAME (c.crtroom_location) crtroom_location_name FROM cont_match m, crtroom c WHERE m.crtroom_no = c.crtroom_no AND TRUNC(start_date) = TRUNC(#{matchDate}) and ntrp = #{ntrp} and crtroom_location = #{crtroomLocation} and progState = #{progState} ORDER BY time
) A) b where rn between (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

select count(*)
FROM cont_match m, crtroom c
WHERE m.crtroom_no = c.crtroom_no
AND TRUNC(start_date) = TRUNC(#{matchDate})
and ntrp = #{ntrp}
and crtroom_location = #{crtroomLocation}
and progState = #{progState}

select * from ( select A.* , rownum rn from (
SELECT m.match_no , m.mem_id , m.match_date , m.time , m.prog_state , FIND_CATEGORY_NAME (m.prog_state) prog_state_name , m.ntrp , FIND_CATEGORY_NAME (m.ntrp) ntrp_name , m.lv , m.gen , FIND_CATEGORY_NAME (m.gen) gen_name , m.rsprice , m.recruit_psn_num , m.detail_ctt , m.crtroom_no , m.age , c.crtroom_name , c.crtroom_location , FIND_CATEGORY_NAME (c.crtroom_location) crtroom_location_name FROM starter_match m, crtroom c WHERE m.crtroom_no = c.crtroom_no AND TRUNC (match_date) = TRUNC(#{matchDate}) and gen = #{gen} and ntrp = #{ntrp} and crtroom_location = #{crtroomLocation} and progState = #{progState} ORDER BY time
) A) b where rn between (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

select count(*)
FROM starter_match m, crtroom c
WHERE m.crtroom_no = c.crtroom_no
AND TRUNC (match_date) = TRUNC(#{matchDate})
and gen = #{gen}
and ntrp = #{ntrp}
and crtroom_location = #{crtroomLocation}
and progState = #{progState}

