mapper.update:
UPDATE match
SET
match_no = #{matchNo}
, match_date = #{matchDate}
, time = #{time}
, game_way = #{gameWay}
, ntrp = #{ntrp}
, lv = #{lv}
, gen = #{gen}
, crt_use_amt = #{crtUseAmt}
, recruit_psn_num = #{recruitPsnNum}
, detail_ctt = #{detailCtt}
, crtroom_no = #{crtroomNo}
, age = #{age}
, update_date = sysdate
WHERE match_no = #{matchNo}

UPDATE club_match
SET
match_no = #{matchNo}
, club_name = #{clubNmae}
, match_date = #{matchDate}
, time = #{time}
, game_way = #{gameWay}
, recruit_psn_num = #{recruitPsnNum}
, club_no = #{clubNo}
, ntrp = #{ntrp}
, lv = #{lv}
, crt_use_amt = #{crtUseAmt}
, detail_ctt = #{detailCtt}
, crtroom_no = #{crtroomNo}
, age = #{age}
WHERE match_no = #{matchNo}

UPDATE cont_match
SET
match_no = #{matchNo}
