mapper.select:
SELECT pwd
FROM MEM
WHERE MEM_ID = #{memId}

SELECT mem_id
FROM MEM
WHERE PHONE_NO = #{phoneNo}

mapper.update:
UPDATE MEM 
SET		
	PWD = #{pwdUpdate}		
WHERE mem_id = #{memId}
AND phone_no = #{phoneNo}

mapper.insert:


	SELECT NVL(MAX(MEM_NO), 0) + 1
	FROM MEM

INSERT ALL
INTO MEM
(
MEM_NO
,MEM_ID
,NAME
,PWD
,PHONE_NO
,MEM_DIV

)
VALUES
(
#{memNo}
,#{memId}
,#{name}
,#{pwd}
,#{phoneNo}
,#{memDiv}
)
INTO PR
(
MEM_ID
,NICK
,AGE
,LV
,REGION
,GEN
,NTRP
,PREFER_GAME_WAY
,PREFER_AGE
,CRTROOM_NO
,PR_IMAGE
,MANNER_POINT
)
VALUES
(
#{memId}
,#{nick}
,#{age}
,#{lv}
,#{region}
,#{gen}
,#{ntrp}
,#{preferGameWay}
,#{preferAge}
,#{crtroomNo}
,#{prImage}
,0
)

	INTO CRTROOM_HOST
	(
	MEM_ID
	,HOST_BANK
	,HOST_ACT_NO
	,HOST_DEPOSITOR
	,HOST_BUSINESS_REGI_NO
	)
	VALUES
	(
	#{memId}
	,#{hostBank}
	,#{hostActNo}
	,#{hostDepositor}
	,#{hostBusinessRegiNo}
	)


SELECT * FROM DUAL

mapper.update:
