mapper.select:
  		SELECT  c.*
		FROM    cart c
		        JOIN    prod_detail d
		        ON      c.prod_detail_no = d.prod_detail_no
		        JOIN    prod p
		        ON      p.prod_no = d.prod_no
		WHERE   mem_id = #{memId}
		AND		d.cate_color = #{cateColor}
		AND		d.cate_size = #{cateSize}

mapper.update:
  		UPDATE 	cart
		SET		cart_prod_qt = #{cartProdQt}
  		WHERE 	cart_no = #{cartNo}
  		AND		mem_id = #{memId}
