mapper.select:
select * from ( select A.* , rownum rn from (
select brd_wrt_no , brd_title , brd_ctt , brd_write_date , brd_hit , FIND_CATEGORY_NAME(brd_div) brd_div_name , m.mem_id , m.name from brd b, mem m where b.mem_id = m.mem_id and brd_div ='bc2' order by brd_wrt_no DESC
) A) b where rn between (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

mapper.insert:


  	 		SELECT NVL(MAX(brd_wrt_no), 0) + 1 
			FROM brd

  	 	INSERT INTO brd
  	 			(
  	 				brd_wrt_no
	 			   , brd_title
	 			   , brd_ctt
	 			   , brd_write_date
	 			   , mem_id
	 			   , brd_div
  	 			)
  	 			VALUES
  	 			(
  	 				#{brdWrtNo}
  	 				,#{brdTitle}
  	 				,#{brdCtt}
 					, SYSDATE
  	 				,#{memId}
  	 				,'bc2'
  	 			)

mapper.update:
		UPDATE brd
         SET
            brd_title = #{brdTitle}
            , brd_ctt = #{brdCtt}
            , brd_edit_date = SYSDATE
      WHERE brd_wrt_no = #{brdWrtNo}
