mapper.insert:


	SELECT(SELECT NVL(MAX(sns_wrt_no), 0) + 1
	FROM sns) sns_wrt_no,
	(SELECT NVL(MAX(sns_grp_no),0)+1
	FROM sns_grp) sns_grp_no
	FROM dual

INSERT ALL
INTO sns_grp
(
sns_grp_no
,grp_create_date
,post_num
,grp_name
)
VALUES
(
#{snsGrpNo}
,sysdate
,#{postNum}
,#{grpName}
)
INTO sns
(
sns_wrt_no
,sns_title
,sns_ctt
,sns_tag
,mem_id
,sns_hit
,sns_write_date
,sns_grp_no
)
VALUES
(
#{snsWrtNo}
,#{snsTitle}
,#{snsCtt}
,#{snsTag}
,#{memId}
,#{snsHit}
,sysdate
,#{snsGrpNo}
)
SELECT * FROM dual

mapper.select:
select like_no
from sns_like
where sns_wrt_no = #{snsWrtNo}
and mem_id = #{memId}

mapper.insert:

	select nvl(max(like_no),0)+1
	from sns_like

INSERT INTO sns_like
(
like_no
,mem_id
,sns_wrt_no
)
VALUES
(
#{likeNo}
,#{memId}
,#{snsWrtNo}
)



	SELECT NVL(MAX(sns_grp_no), 0) + 1
	FROM sns_grp

INSERT INTO sns_grp
(
sns_grp_no
,grp_create_date
,grp_name
)
VALUES
(
#{snsGrpNo}

	,#{grpCreateDate}
	,sysdate

,#{grpName}
)

mapper.update:
update sns_grp
	set  g.post_num = #{postNum}
        ,g.grp_name = #{grpName}
        ,g.grp_edit_date = #{grpEditDate}
   where sns_grp_no = #{snsGrpNo}
