mapper.insert:


	SELECT NVL(MAX(sns_wrt_no), 0) + 1
	FROM sns

INSERT INTO sns
(
	sns_wrt_no
	,sns_title
	,sns_ctt
	,sns_tag
	,mem_id
	,sns_hit
	,sns_write_date
	,sns_grp_no
)
VALUES
(
	#{snsWrtNo}
	,#{snsTitle}
	,#{snsCtt}
	,#{snsTag}
	,#{memId}
	,#{snsHit}
	,sysdate
	,#{snsGrpNo}
)


	select nvl(max(like_no),0)+1
	from sns_like

INSERT INTO sns_like
(
	like_no
	,mem_id
	,sns_wrt_no
)
VALUES
(
	#{likeNo}
	,#{memId}
	,#{snsWrtNo}
)



	SELECT NVL(MAX(sns_grp_no), 0) + 1
	FROM sns_grp

INSERT INTO sns_grp
	(
		sns_grp_no
		,grp_create_date
		,grp_name
		,mem_id
	)
VALUES
	(
		#{snsGrpNo}
		,sysdate
		,#{grpName}
		,#{memId}
	)

mapper.update:
update sns_grp
	set  grp_name = #{grpName}
        ,grp_edit_date = sysdate
   where sns_grp_no = #{snsGrpNo}

update sns
	set  sns_title = #{snsTitle}
        ,sns_ctt = #{snsCtt}
        ,sns_tag = #{snsTag}
        ,sns_grp_no = #{snsGrpNo}
  where sns_wrt_no = #{snsWrtNo}

mapper.delete:
{ call DELETE_GRP(
		#{snsGrpNo, mode=IN, jdbcType=DECIMAL},
		#{delGrpResult, mode=OUT, jdbcType=DECIMAL, javaType=long}	
	)
}

	    {call DELETE_SNS(
            #{snsWrtNo, mode=IN, jdbcType=DECIMAL},
            #{delResult, mode=OUT, jdbcType=DECIMAL, javaType=long}
        	)
   		 }
