mapper.insert:


  	 		SELECT NVL(MAX(sns_rep_no), 0) + 1 
			FROM sns_rep

  	 	INSERT INTO sns_rep
  	 			(
  	 				sns_rep_no
	 			   , sns_rep_ctt
	 			   , sns_rep_write_date
	 			   , sns_wrt_no
	 			   , mem_id
  	 			)
  	 			VALUES
  	 			(
  	 				#{snsRepNo}
  	 				,#{snsRepCtt}
 					,sysdate  	 
  	 				,#{snsWrtNo}
  	 				,#{memId}
  	 			)

mapper.update:
UPDATE sns_rep
	SET
		sns_rep_ctt = #{snsRepCtt}
	  , sns_rep_edit_date = sysdate
WHERE sns_rep_no = #{snsRepNo}
